ut\":\"equal\",\"kbVersion\":2} -->\n<!-- wp:kadence/column {\"borderWidth\":[\"\",\"\",\"\",\"\"],\"uniqueID\":\"27_bannerad_1_v2\",\"kbVersion\":2} -->\n<div class=\"wp-block-kadence-column kadence-column27_bannerad_1_v2\"><div class=\"kt-inside-inner-col\"><!-- wp:image {\"linkDestination\":\"custom\"} -->\n<figure class=\"wp-block-image\"><a href=\"#\"><img src=\"https://via.placeholder.com/300x100.png?text=Banner+1\" alt=\"バナー広告1\"/></a></figure>\n<!-- /wp:image --></div></div>\n<!-- /wp:kadence/column -->\n\n<!-- wp:kadence/column {\"borderWidth\":[\"\",\"\",\"\",\"\"],\"uniqueID\":\"27_bannerad_2_v2\",\"kbVersion\":2} -->\n<div class=\"wp-block-kadence-column kadence-column27_bannerad_2_v2\"><div class=\"kt-inside-inner-col\"><!-- wp:image {\"linkDestination\":\"custom\"} -->\n<figure class=\"wp-block-image\"><a href=\"#\"><img src=\"https://via.placeholder.com/300x100.png?text=Banner+2\" alt=\"バナー広告2\"/></a></figure>\n<!-- /wp:image --></div></div>\n<!-- /wp:kadence/column -->\n\n<!-- wp:kadence/column {\"borderWidth\":[\"\",\"\",\"\",\"\"],\"uniqueID\":\"27_bannerad_3_v2\",\"kbVersion\":2} -->\n<div class=\"wp-block-kadence-column kadence-column27_bannerad_3_v2\"><div class=\"kt-inside-inner-col\"><!-- wp:image {\"linkDestination\":\"custom\"} -->\n<figure class=\"wp-block-image\"><a href=\"#\"><img src=\"https://via.placeholder.com/300x100.png?text=Banner+3\" alt=\"バナー広告3\"/></a></figure>\n<!-- /wp:image --></div></div>\n<!-- /wp:kadence/column -->\n<!-- /wp:kadence/rowlayout -->','丹後王国「食のみやこ」','','inherit','closed','closed','','27-revision-v1','','','2025-07-31 21:27:07','2025-07-31 12:27:07','',27,'http://tango-kingdom.local/?p=91',0,'revision','',0);
